SELECT name,chatroom_name,nuc.joined_at from (SELECT DATE_FORMAT(joined_at, '%Y-%m-%d')AS joined_at,user_id,chatroom_id FROM users_chatrooms) as nuc left join users u on nuc.user_id=u.id left join chatrooms cr on nuc.chatroom_id=cr.id where u.is_deleted=0 and cr.is_deleted=0 ORDER BY nuc.joined_at ASC
